import React, { useEffect, useState } from "react";

import { apiDelete, apiGet } from "../utils/api";

import InvoiceTable from "./InvoiceTable";
import { InvoiceFilter } from "./InvoiceFilter";


const InvoiceIndex = (props) => {
    const [invoices, setInvoices] = useState([]);
    const [sellerListState, setSellerList] = useState([]);
    const [buyerListState, setBuyerList] = useState([]);
    const [productListState, setProductList] = useState([]);
    const [filterState, setFilter] = useState({
        buyerID: undefined,
        sellerID: undefined,
        product: undefined,
        minPrice: undefined,
        maxPrice: undefined,
        limit: undefined
        });
    


    const deleteInvoice = async (id) => {
        try {
            await apiDelete("/api/invoices/" + id);
        } catch (error) {
            console.log(error.message);
            alert(error.message)
        }
        setInvoices(invoices.filter((item) => item._id !== id));
    };

    useEffect(() => {
        apiGet('/api/directors').then((data) => setDirectorList(data));
        apiGet('/api/actors').then((data) => setActorList(data));
        apiGet('/api/genres').then((data) => setGenreList(data));
        apiGet('/api/movies').then((data) => setMovies(data));
    }, []);

    useEffect(() => {
        apiGet("/api/invoices").then((data) => setInvoices(data));
    }, [])

    return (
        <div>
            <h1>Seznam faktur</h1>
            <InvoiceTable
                deleteInvoice={deleteInvoice}
                items={invoices}
                label="Počet faktur:"
            />
        </div>
    );
};
export default InvoiceIndex;
