import React, { useEffect, useState } from "react";

import {apiDelete, apiGet} from "../utils/api";

import InvoiceTable from "./InvoiceTble";


const InvoiceIndex = () => {
    const [invoices, setInvoices] = useState([]);
    const [searchQuery, setSearchQuery] = useState("");

    const deleteInvoice = async (id) => {
        try {
            await apiDelete("/api/invoices/" + id);
        } catch(error) {
            console.log(error.message);
            alert(error.message)
        }
        setInvoices(invoices.filter((item) => item._id !== id));
    };

    useEffect(() => {
        apiGet("/api/invoices").then((data) => {
            const invoicesWithIndex = data.map((invoice, index) => ({
                ...invoice,
                originalIndex: index + 1,
            }));
            setInvoices(invoicesWithIndex);
        });
    }, []);

    const filteredInvoices = invoices.filter(invoice => invoice.invoiceNumber === Number(searchQuery));

    return (
        <div>
            <h1>Seznam faktur</h1>
            <InvoiceTable
            deleteInvoice={deleteInvoice}
            items={filteredInvoices}
            label="Počet faktur:"
            searchQuery={searchQuery}
            setSearchQuery={setSearchQuery}
            />
        </div>
    );
};
export default InvoiceIndex;
