import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";

import { apiGet, apiPost, apiPut } from "../utils/api";

import InputField from "../components/InputField";
import InputCheck from "../components/InputCheck";
import FlashMessage from "../components/FlashMessage";
import InputSelect from "../components/InputSelect";



const InvoiceForm = () => {
    const navigate = useNavigate();
    const { id } = useParams();
    const [invoice, setInvoice] = useState({
        invoiceNumber: 0,
        issued: 0,
        dueDate: 0,
        product: "",
        price: "",
        vat: "",
        note: "",
        buyerList: [],
        sellerList: [],
        buyer: [],
        seller: [],
    });
    const [sentState, setSent] = useState(false);
    const [successState, setSuccess] = useState(false);
    const [errorState, setError] = useState(null);
    const [persons, setPersons] = useState([]);
    const [sellerState, setSeller] = useState([]);
    const [buyerState, setBuyer] = useState([]);
    const [sellerListState, setSellerList] = useState([]);
    const [buyerListState, setBuyerList] = useState([]);

    useEffect(() => {
        if (id) {
            apiGet("/api/invoices/" + id).then((data) => setInvoice(data));
        }
    }, [id]);

    useEffect(() => {
        apiGet("/api/persons")
            .then(data => {
                console.log("Fetched persons:", data); // <-- Add this line
                setPersons(data);
            })
            .catch(error => console.error('Error fetching persons:', error));
    }, []);

    const handleSubmit = (e) => {
        e.preventDefault();


        (id ? apiPut("/api/invoices/" + id, payload) : apiPost("/api/invoices", payload))
            .then((data) => {
                setInvoice({
                    ...data,
                });
                console.log("Response from server:", data);
                setSent(true);
                setSuccess(true);
                navigate("/invoices");

            })
            .catch((error) => {
                console.error("Error submitting:", error.message);
                setError(error.message);
                setSent(true);
                setSuccess(false);
            });
    };

    const handleSelectChange = (e) => {
        const target = e.target;
        const value = target.value;
        const name = target.name;
    
        const selectedPerson = persons.find(person => person._id.toString() === value);
    
        console.log(`Select changed - name: ${name}, value: ${value}, selectedPerson:`, selectedPerson);
      
        if (name === 'seller') {
            setSeller(selectedPerson);
        } else if (name === 'buyer') {
            setBuyer(selectedPerson);
        }
    };

    const sent = sentState;
    const success = successState;

    return (
        <div>
            <h1>{id ? "Upravit" : "Vytvořit"} faktura</h1>
            <hr />
            {errorState ? (
                <div className="alert alert-danger">{errorState}</div>
            ) : null}
            {sent && (
                <FlashMessage
                    theme={success ? "success" : ""}
                    text={success ? "Uložení faktury proběhlo úspěšně" : ""}
                />
            )}

            <form onSubmit={handleSubmit}>
                <InputField
                    required={true}
                    type="number"
                    name="invoiceNumber"
                    min="3"
                    label="Číslo faktury"
                    prompt="Zadejte číslo faktury"
                    value={invoice.invoiceNumber}
                    handleChange={(e) => {
                        setInvoice({ ...invoice, invoiceNumber: e.target.value });
                    }}
                />
                <InputField
                    required={true}
                    type="date"
                    name="issued"
                    min="3"
                    label="Datum vystavení"
                    prompt="Zadejte datum vystavení faktury"
                    value={invoice.issued}
                    handleChange={(e) => {
                        setInvoice({ ...invoice, issued: e.target.value });
                    }}
                />
                <InputField
                    required={true}
                    type="date"
                    name="dueDate"
                    min="3"
                    label="Datum splatnosti"
                    prompt="Zadejte datum splatnosti faktury"
                    value={invoice.dueDate}
                    handleChange={(e) => {
                        setInvoice({ ...invoice, dueDate: e.target.value });
                    }}
                />
                <InputField
                    required={true}
                    type="text"
                    name="product"
                    min="3"
                    label="Název produktu"
                    prompt="Zadejte název produktu"
                    value={invoice.product}
                    handleChange={(e) => {
                        setInvoice({ ...invoice, product: e.target.value });
                    }}
                />
                <InputField
                    required={true}
                    type="number"
                    name="price"
                    min="1"
                    label="Cena"
                    prompt="Zadejte cenu"
                    value={invoice.price}
                    handleChange={(e) => {
                        setInvoice({ ...invoice, price: e.target.value });
                    }}
                />
                <InputField
                    required={true}
                    type="number"
                    name="vat"
                    min="1"
                    label="DPH"
                    prompt="Zadejte DPH"
                    value={invoice.vat}
                    handleChange={(e) => {
                        setInvoice({ ...invoice, vat: e.target.value });
                    }}
                />
                <InputField
                    required={true}
                    type="text"
                    name="note"
                    label="Poznámka"
                    value={invoice.note}
                    handleChange={(e) => {
                        setInvoice({ ...invoice, note: e.target.value });
                    }}
                />
                <InputSelect
    label="Prodávající"
    name="seller"
    value={seller?._id}
    handleChange={handleSelectChange}
    items={persons}
    prompt="Vyberte prodávajícího"
    required
/>

<InputSelect
    label="Kupující"
    name="buyer"
    value={buyer?._id}
    handleChange={handleSelectChange}
    items={persons}
    prompt="Vyberte kupujícího"
    required
/>

                <input type="submit" className="btn btn-primary" value="Uložit" />
            </form>
        </div>
    );
};
export default InvoiceForm;
