import React, { useEffect, useState } from "react";

import { apiDelete, apiGet } from "../utils/api";

import InvoiceTable from "./InvoiceTable";
import { InvoiceFilter } from "./InvoiceFilter";


const InvoiceIndex = (props) => {
    const [invoices, setInvoices] = useState([]);
    const [filteredInvoices, setFilteredInvoices] = useState([]);
    const [sellerListState, setSellerList] = useState([]);
    const [buyerListState, setBuyerList] = useState([]);
    const [productListState, setProductList] = useState([]);
    const [ProductIndexes, setProductIndexes] = useState([])
    const [filterState, setFilter] = useState({
        buyerID: undefined,
        sellerID: undefined,
        product: undefined,
        minPrice: undefined,
        maxPrice: undefined,
        limit: undefined
    });



    const deleteInvoice = async (id) => {
        try {
            await apiDelete("/api/invoices/" + id);
            setInvoices(invoices.filter((item) => item._id !== id));
            setFilteredInvoices(filteredInvoices.filter((item) => item._id !== id));
        } catch (error) {
            console.log(error.message);
            alert(error.message);
        }
    };



    useEffect(() => {
        apiGet("/api/invoices").then((data) => {
            setInvoices(data);
            setFilteredInvoices(data); // Initialize with full invoice data

            // Setting unique sellers
            const sellers = data.map(invoice => invoice.seller);
            const uniqueSellers = Array.from(new Set(sellers.map(seller => seller._id)))
                .map(id => sellers.find(seller => seller._id === id));
            setSellerList(uniqueSellers);

            // Setting unique buyers
            const buyers = data.map(invoice => invoice.buyer);
            const uniqueBuyers = Array.from(new Set(buyers.map(buyer => buyer._id)))
                .map(id => buyers.find(buyer => buyer._id === id));
            setBuyerList(uniqueBuyers);

            // Setting unique products
            const uniqueProducts = Array.from(new Set(data.map(invoice => invoice.product)));
            const productsIndexes = uniqueProducts.map((_, index) => index);
            setProductList(uniqueProducts);
            setProductIndexes(productsIndexes);
        });
    }, []);

    const handleChange = (e) => {
        setFilter(prevState => ({
            ...prevState,
            [e.target.name]: e.target.value === '' ? undefined : e.target.value
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        // Apply each filter to the full invoices list
        let results = [...invoices];
        
        if (filterState.buyerID) {
            results = results.filter(invoice => invoice.buyer._id === filterState.buyerID);
        }
        if (filterState.sellerID) {
            results = results.filter(invoice => invoice.seller._id === filterState.sellerID);
        }
        if (filterState.product) {
            results = results.filter(invoice => invoice.product === filterState.product);
        }
        if (filterState.minPrice) {
            results = results.filter(invoice => invoice.price >= Number(filterState.minPrice));
        }
        if (filterState.maxPrice) {
            results = results.filter(invoice => invoice.price <= Number(filterState.maxPrice));
        }
        if (filterState.limit) {
            results = results.slice(0, Number(filterState.limit));
        }

        setFilteredInvoices(results); // Update filtered invoices based on criteria
    };

    return (
        <div>
            <h1>Seznam faktur</h1>
            <hr />

            <InvoiceFilter
                handleChange={handleChange}
                handleSubmit={handleSubmit}
                buyerList={buyerListState}
                sellerList={sellerListState}
                productList={ProductIndexes}
                filter={filterState}
                confirm="Filtrovat faktury"
                enumProductList={productListState}
            />
            <hr />
            <InvoiceTable
                deleteInvoice={deleteInvoice}
                items={invoices}
                label="PoÄet faktur:"
            />
        </div>
    );
};
export default InvoiceIndex;
