import React from "react";


export function PersonSelect({ label, value, onChange, persons}){
    return(
        <div>
            <label>{label}:</label>
            <select value={value || ""} onChange={onChange} required className="browser-default form-select">
                <option value=""> Vyberte {label.toLowerCase()}</option>
                {persons.map(person => (
                    <option key={person._id} value={person._id}>
                        {person.name}
                    </option>
                ))}
            </select>
        </div>
    );
}
